func main() {
	r := gin.Default()

	r.SetFuncMap(template.FuncMap{"upper": strings.ToUpper})
	r.LoadHTMLGlob("templates/*.html")

	r.GET("/", func(content *gin.Context) {
		text, b := content.GetQuery("type")

		content.HTML(http.StatusOK, "index.html", gin.H{
			"image": setImage(text, b),
			"text":  text,

			"js":         javaScript,
			"golang":     golang,
			"python":     python,
			"csharp":     csharp,
			"c":          c,
			"cpp":        cpp,
			"groovy":     groovy,
			"ruby":       ruby,
			"kotlin":     kotlin,
			"java":       java,
			"typeScript": typeScript,
			"spring":     spring,
			"springBoot": springBoot,
			"unity":      unity,
			"unreal":     unreal,
			"vue":        vue,
			"node":       node,
			"nodeRed":    nodeRed,
			"vscode":     vscode,
			"apache":     apache,
		})
	})

	_ = r.Run()
}

func setImage(lang string, b bool) string {
	if !b {
		return ""
	}

	if lang == "js" {
		return javaScript
	} else if lang == "golang" {
		return golang
	} else if lang == "python" {
		return python
	} else if lang == "csharp" {
		return csharp
	} else if lang == "c" {
		return c
	} else if lang == "cpp" {
		return cpp
	} else if lang == "groovy" {
		return groovy
	} else if lang == "ruby" {
		return ruby
	} else if lang == "kotlin" {
		return kotlin
	} else if lang == "java" {
		return java
	} else if lang == "typeScript" {
		return typeScript
	} else if lang == "spring" {
		return spring
	} else if lang == "springBoot" {
		return springBoot
	} else if lang == "unity" {
		return unity
	} else if lang == "unreal" {
		return unreal
	} else if lang == "vue" {
		return vue
	} else if lang == "node" {
		return node
	} else if lang == "nodeRed" {
		return nodeRed
	} else if lang == "vscode" {
		return vscode
	} else if lang == "apache" {
		return apache
	} else {
		return ""
	}
}
